import React, { useState, useEffect, useRef } from "react";

const widgetStyle: React.CSSProperties = {
  position: "fixed",
  bottom: "20px",
  right: "20px",
  width: "400px",
  backgroundColor: "#1e1e1e",
  color: "#c5c6c7",
  fontFamily: "'SF Pro Display', -apple-system, sans-serif",
  fontSize: "12px",
  borderRadius: "12px",
  boxShadow: "0 0 12px rgba(102, 252, 241, 0.4)",
  zIndex: 10000,
  userSelect: "none",
  cursor: "move",
  padding: "12px",
  border: "1px solid #45a29e",
};

const headerStyle: React.CSSProperties = {
  fontWeight: 700,
  marginBottom: "10px",
  borderBottom: "1px solid #45a29e",
  paddingBottom: "6px",
  color: "#66fcf1",
  textShadow: "0 0 4px #66fcf1",
};

const flowItemStyle: React.CSSProperties = {
  marginBottom: "6px",
  padding: "6px",
  backgroundColor: "#0b0c10",
  borderRadius: "6px",
  border: "1px solid #45a29e",
  color: "#66fcf1",
};

const SystemFlowWidget: React.FC = () => {
  const [position, setPosition] = useState({ x: 20, y: 20 });
  const [dragging, setDragging] = useState(false);
  const dragStart = useRef<{ x: number; y: number } | null>(null);
  const widgetRef = useRef<HTMLDivElement>(null);

  const [flowStates, setFlowStates] = useState({
    backend: "Idle",
    rasaAgent: "Waiting for input",
    randomForest: "Idle",
    llamaAgent: "Idle",
  });

  useEffect(() => {
    const interval = setInterval(() => {
      setFlowStates((prev) => {
        const nextStates = { ...prev };
        if (prev.rasaAgent === "Waiting for input") {
          nextStates.rasaAgent = "Classifying intent";
          nextStates.llamaAgent = "Idle";
          nextStates.randomForest = "Predicting intent";
          nextStates.backend = "Idle";
        } else if (prev.rasaAgent === "Classifying intent") {
          nextStates.rasaAgent = "Routing to LLaMA fallback";
          nextStates.llamaAgent = "Generating response";
          nextStates.randomForest = "Idle";
          nextStates.backend = "Processing CRUD";
        } else {
          nextStates.rasaAgent = "Waiting for input";
          nextStates.llamaAgent = "Idle";
          nextStates.randomForest = "Idle";
          nextStates.backend = "Idle";
        }
        return nextStates;
      });
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const onMouseDown = (e: React.MouseEvent) => {
    setDragging(true);
    dragStart.current = {
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    };
  };

  const onMouseMove = (e: MouseEvent) => {
    if (dragging && dragStart.current) {
      setPosition({
        x: e.clientX - dragStart.current.x,
        y: e.clientY - dragStart.current.y,
      });
    }
  };

  const onMouseUp = () => {
    setDragging(false);
    dragStart.current = null;
  };

  useEffect(() => {
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
    return () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };
  }, [dragging]);

  const isActive = (key: string) => flowStates[key as keyof typeof flowStates] !== "Idle" && flowStates[key as keyof typeof flowStates] !== "Waiting for input";

  return (
    <div
      ref={widgetRef}
      style={{ ...widgetStyle, left: position.x, top: position.y }}
      onMouseDown={onMouseDown}
      className="relative"
    >
      <style>
        {`
          .glow {
            filter: drop-shadow(0 0 5px #66fcf1);
          }
          .path-glow {
            stroke: #66fcf1;
            stroke-width: 2;
            stroke-dasharray: 5,5;
            filter: drop-shadow(0 0 3px #66fcf1);
          }
        `}
      </style>
      <div style={headerStyle}>System Flow Monitor</div>
      <svg width="100%" height="100" className="mb-4">
        {/* Rasa Agent (Circle) */}
        <circle
          cx="50"
          cy="50"
          r="20"
          fill="#0b0c10"
          stroke={isActive("rasaAgent") ? "#66fcf1" : "#1f6b68"}
          strokeWidth="2"
          className={isActive("rasaAgent") ? "glow animate-pulse" : "glow"}
        />
        <text x="40" y="55" fill="#66fcf1" fontSize="9">Rasa</text>

        {/* LLaMA Agent (Diamond) */}
        <polygon
          points="110,50 130,60 110,70 90,60"
          fill="#0b0c10"
          stroke={isActive("llamaAgent") ? "#66fcf1" : "#1f6b68"}
          strokeWidth="2"
          className={isActive("llamaAgent") ? "glow animate-pulse" : "glow"}
        />
        <text x="100" y="55" fill="#66fcf1" fontSize="9">LLaMA</text>

        {/* Random Forest (Hexagon) */}
        <polygon
          points="190,40 210,20 210,60 190,80 170,60 170,20"
          fill="#0b0c10"
          stroke={isActive("randomForest") ? "#66fcf1" : "#1f6b68"}
          strokeWidth="2"
          className={isActive("randomForest") ? "glow animate-pulse" : "glow"}
        />
        <text x="180" y="45" fill="#66fcf1" fontSize="9">RF</text>

        {/* Backend (Rectangle) */}
        <rect
          x="270"
          y="20"
          width="80"
          height="40"
          rx="4"
          fill="#0b0c10"
          stroke={isActive("backend") ? "#66fcf1" : "#1f6b68"}
          strokeWidth="2"
          className={isActive("backend") ? "glow animate-pulse" : "glow"}
        />
        <text x="280" y="40" fill="#66fcf1" fontSize="9">Backend</text>

        {/* Circuit-like Connections */}
        <path
          d="M70 50 H90"
          className={isActive("rasaAgent") && isActive("llamaAgent") ? "path-glow" : ""}
          fill="none"
        >
          <animate
            attributeName="stroke-dashoffset"
            from="0"
            to="10"
            dur="0.5s"
            repeatCount="indefinite"
            begin={isActive("rasaAgent") && isActive("llamaAgent") ? "0s" : "none"}
          />
        </path>
        <path
          d="M130 60 H150 V40 H170"
          className={isActive("llamaAgent") && isActive("randomForest") ? "path-glow" : ""}
          fill="none"
        >
          <animate
            attributeName="stroke-dashoffset"
            from="0"
            to="10"
            dur="0.5s"
            repeatCount="indefinite"
            begin={isActive("llamaAgent") && isActive("randomForest") ? "0s" : "none"}
          />
        </path>
        <path
          d="M70 50 H150 V40 H170"
          className={isActive("rasaAgent") && isActive("randomForest") ? "path-glow" : ""}
          fill="none"
        >
          <animate
            attributeName="stroke-dashoffset"
            from="0"
            to="10"
            dur="0.5s"
            repeatCount="indefinite"
            begin={isActive("rasaAgent") && isActive("randomForest") ? "0s" : "none"}
          />
        </path>
        <path
          d="M210 40 H270"
          className={isActive("randomForest") && isActive("backend") ? "path-glow" : ""}
          fill="none"
        >
          <animate
            attributeName="stroke-dashoffset"
            from="0"
            to="10"
            dur="0.5s"
            repeatCount="indefinite"
            begin={isActive("randomForest") && isActive("backend") ? "0s" : "none"}
          />
        </path>
      </svg>
      <div style={flowItemStyle}>
        <strong>Backend:</strong> {flowStates.backend}
      </div>
      <div style={flowItemStyle}>
        <strong>Rasa Agent:</strong> {flowStates.rasaAgent}
      </div>
      <div style={flowItemStyle}>
        <strong>Random Forest ML:</strong> {flowStates.randomForest}
      </div>
      <div style={flowItemStyle}>
        <strong>LLaMA Agent:</strong> {flowStates.llamaAgent}
      </div>
    </div>
  );
};

export default SystemFlowWidget;
